DB::insert("INSERT INTO `creatures` (`name`, `page`, `challenge_rating`, `desc`, `speed`, `armor_class`, `armor_class_notes`, `hit_dice`, `initiative`, `str`, `dex`, `con`, `int`, `wis`, `cha`, `str_save`, `dex_save`, `con_save`, `int_save`, `wis_save`, `cha_save`, `senses`, `special`, `size`, `environment`, `skills`, `legendary`, `legendary_actions`, `attacks`, `image`, `user_id`, `id`, `multiattacks`) VALUES  ('Death dog','321','1',NULL,'40ft.','12',NULL,'6d8+12',2,2,2,2,-4,1,-2,2,2,2,-4,1,-2,'darkvision 120ft., passive Perception 15','','Medium Monstrosity','desert,grassland,underground','[{ \"name\": \"Perception\", \"bonus\": \"5\"},{ \"name\": \"Stealth\", \"bonus\": \"4\"}]',NULL,'[]','[{\"name\": \"Bite\", \"bonus\": \"4\", \"damage\": \"1d6+2 🏹\", \"special\": \"lf the target is a creature, it must succeed on a DC 12 Constitution saving throw against disease or become poisoned until the disease is cured. Every 24 hours that elapse, the creature must repeat the saving throw, reducing its hit point maximum by 5 (ldlO) on a failure. This reduction lasts until the disease is cured. The creature dies if the disease reduces its hit point maximum to 0.\"}]','Death dog.png'
